import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useStateValue } from "../../../StateProvider";
import { actionTypes } from "../../../reducer";
import Avatar from "@mui/material/Avatar";
import CloseIcon from "@mui/icons-material/Close";
import TinderCard from "react-tinder-card";
import ReactPlayer from "react-player"
import VideoPlayer from "./VideoPlayer";
import { ArrowBack } from "@material-ui/icons";
import {useHistory} from "react-router-dom"



function StoryPage() {
    const [{ openStoryPopup, startJourney }, dispatch] = useStateValue();
    const history = useHistory()

  const [parts, setParts] = useState([
    {
      imageUrl:
        "data:image/jpeg;base64,",
      caption: "Progressing facebook as it is just the start.. ",
    },
    {
      imageUrl:
        "data:image/jpeg;base64,",
      caption: "Facebook grows very much in 5 years",
    },
    {
      imageUrl:
        "data:image/jpeg;base64,",
      caption: "Start of facebook through web D in 2004",
    },
  ]);

  useEffect(() => {}, [startJourney]);

  const close_popup = () => {
    dispatch({
      type: actionTypes.OPEN_STORY_POPUP,
      openStoryPopup: false,
    });
  };

  const start_journey = () => {
    dispatch({
      type: actionTypes.START_JOURNEY,
      startJourney: true,
    });
  };

  const stop_journey = () => {
    dispatch({
      type: actionTypes.START_JOURNEY,
      startJourney: false,
    })
  };

  const swiped = (index) => {
     console.log("Index is " , index);
     if(index === 0) {
        dispatch({
            type: actionTypes.START_JOURNEY,
            startJourney: false,
          })
     }
  }

  const goBack = () => {
    history.goBack();
  }



    return (
        <div>
          <Container>
          <div className="storyPopup_header">
              <div className="user_info">
                <ArrowBack className = "back_icon" onClick = {goBack}/>
                <Avatar
                  className="user_info_avatar"
                  src="https://bsmedia.business-standard.com/_media/bs/img/article/2018-03/22/full/1521664011-0145.jpg"
                />
                <p>Mark</p>
              </div>
              
            </div>
            {startJourney === false ? (
              <div className="journey"></div>
            ) : (
              <div className="journey_cards">
                <div className="tinderCards_cardContainer">
                  {parts.map((part, index) => (
                    <TinderCard
                      className="swipe"
                      key={part.caption}
                      preventSwipe={["up", "down", "right"]}
                      onSwipe = {() => swiped(index)}
                      //  onCardLeftScreen = {() => outOfFrame(person.name)}
                    >
                      <div
                        className="card"
                        style={{ backgroundImage: `url(${part.imageUrl})` }}
                      >
                        <h3>{part.caption}</h3>
                      </div>
                    </TinderCard>
                  ))}
                </div>
              </div>  
            
            
            // <div style = {{
            //     marginTop : 20,
            //     marginBottom : 20,
            // }}>
            //     <VideoPlayer/>
            // </div>
            )}
            <div className="start_button">
              {startJourney === false ? (
                <button onClick={start_journey} className="start">Start Journey</button>
              ) : (
                <button className = "started" onClick = {stop_journey}>Stop</button>
              )}
            </div>
            <div className="achievements">
              <div className="achievement">
                <img
                  src="data:image/png;base64,"
                  alt=""
                />
                <p>
                  Completed 2 months internship as frontend web developer at
                  Deinfra
                </p>
              </div>
              <div className="achievement">
                <img
                  src="data:image/png;base64,"
                  alt=""
                />
                <p>
                  Completed 2 months internship as frontend web developer at
                  Deinfra
                </p>
              </div>
              <div className="achievement">
                <img
                  src="data:image/png;base64,"
                  alt=""
                />
                <p>
                  Completed 2 months internship as frontend web developer at
                  Deinfra
                </p>
              </div>
            </div> 
          </Container>  
        </div>
    )
};

